import React, { useState, useEffect } from 'react';
import { getAssets, depreciateAssets } from '../services/api';

const Assets: React.FC = () => {
  const [assets, setAssets] = useState<any[]>([]);
  const [deprResult, setDeprResult] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    (async () => {
      const res = await getAssets();
      if (res.success) setAssets(res.data || []);
    })();
  }, []);

  const handleDepreciate = async (assetId?: number) => {
    setLoading(true);
    const ids = assetId ? [assetId] : assets.map(a => a.id);
    const res = await depreciateAssets(ids);
    if (res.success) setDeprResult(res.data || []);
    setLoading(false);
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">고정자산 관리</h2>
      <button onClick={() => handleDepreciate()} className="mb-4 px-4 py-2 bg-blue-600 text-white rounded" disabled={loading}>
        {loading ? '계산 중...' : '전체 감가상각'}
      </button>
      <table className="min-w-full table-fixed border mb-8">
        <thead>
          <tr>
            <th>자산명</th>
            <th>취득가</th>
            <th>취득일</th>
            <th>감가상각방법</th>
            <th>잔존가치</th>
            <th>작업</th>
          </tr>
        </thead>
        <tbody>
          {assets.map(asset => (
            <tr key={asset.id}>
              <td>{asset.name}</td>
              <td>{asset.purchase_value}</td>
              <td>{asset.purchase_date}</td>
              <td>{asset.depreciation_method}</td>
              <td>{asset.current_value}</td>
              <td>
                <button onClick={() => handleDepreciate(asset.id)} className="px-2 py-1 bg-gray-100 rounded">감가상각</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 className="font-bold mb-2">감가상각 결과 (분개장)</h3>
      <table className="min-w-full table-fixed border">
        <thead>
          <tr>
            <th>자산명</th>
            <th>감가상각액</th>
            <th>일자</th>
            <th>전표번호</th>
            <th>분개라인</th>
          </tr>
        </thead>
        <tbody>
          {deprResult.map((res, idx) => (
            <tr key={idx}>
              <td>{assets.find(a => a.id === res.asset_id)?.name}</td>
              <td>{res.depreciation_amount}</td>
              <td>{res.date}</td>
              <td>{res.journal_entry?.ref}</td>
              <td>
                {res.journal_entry?.lines?.map((line, i) => (
                  <div key={i}>
                    {line.account_name}: 차변 {line.debit}, 대변 {line.credit}
                  </div>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Assets;
