import React, { useState, useEffect } from 'react';
import { PlusIcon, EyeIcon, PencilIcon, TrashIcon, XMarkIcon, PlusCircleIcon, MinusCircleIcon } from '@heroicons/react/24/outline';
import { getJournalEntries, createJournalEntry, updateJournalEntry, deleteJournalEntry, getAccounts } from '../services/api.ts';
import { JournalEntry, JournalLine, Account } from '../types';

interface JournalLineForm {
  account_id: number;
  name: string;
  debit: number;
  credit: number;
}

interface JournalEntryFormData {
  name: string;
  date: string;
  ref: string;
  debit_account_id: number;
  debit_name: string;
  debit_amount: number;
  credit_account_id: number;
  credit_name: string;
  credit_amount: number;
}

const JournalEntries: React.FC = () => {
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [accounts, setAccounts] = useState<Account[]>([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState<JournalEntry | null>(null);
  const [editingEntry, setEditingEntry] = useState<JournalEntry | null>(null);
  const [formData, setFormData] = useState<JournalEntryFormData>({
    name: '',
    date: new Date().toISOString().split('T')[0],
    ref: '',
    lines: [{ account_id: 0, name: '', debit: 0, credit: 0 }]
  });
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [entriesResponse, accountsResponse] = await Promise.all([
        getJournalEntries(),
        getAccounts()
      ]);
      
      if (entriesResponse.success) {
        setEntries(entriesResponse.data || []);
      }
      
      if (accountsResponse.success) {
        setAccounts(accountsResponse.data || []);
      }
    } catch (error) {
      console.error('데이터 조회 실패:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      date: new Date().toISOString().split('T')[0],
      ref: '',
      debit_account_id: 0,
      debit_name: '',
      debit_amount: 0,
      credit_account_id: 0,
      credit_name: '',
      credit_amount: 0
    });
    setEditingEntry(null);
  };

  const handleOpenModal = (entry?: JournalEntry) => {
    if (entry) {
      setEditingEntry(entry);
      // 기존 분개장의 라인 데이터를 가져와서 차변/대변으로 분리
      const debitLine = entry.lines?.find(line => line.debit > 0);
      const creditLine = entry.lines?.find(line => line.credit > 0);
      
      setFormData({
        name: entry.name,
        date: entry.date,
        ref: entry.ref || '',
        debit_account_id: debitLine?.account_id || 0,
        debit_name: debitLine?.name || '',
        debit_amount: debitLine?.debit || 0,
        credit_account_id: creditLine?.account_id || 0,
        credit_name: creditLine?.name || '',
        credit_amount: creditLine?.credit || 0
      });
    } else {
      resetForm();
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    resetForm();
  };

  const handleDebitAccountChange = (accountId: number) => {
    const selectedAccount = accounts.find(acc => acc.id === accountId);
    setFormData({
      ...formData,
      debit_account_id: accountId,
      debit_name: selectedAccount?.name || ''
    });
  };

  const handleCreditAccountChange = (accountId: number) => {
    const selectedAccount = accounts.find(acc => acc.id === accountId);
    setFormData({
      ...formData,
      credit_account_id: accountId,
      credit_name: selectedAccount?.name || ''
    });
  };

  const validateForm = () => {
    // 분개 라인 검증
    const totalDebit = formData.lines.reduce((sum, line) => sum + (line.debit || 0), 0);
    const totalCredit = formData.lines.reduce((sum, line) => sum + (line.credit || 0), 0);
    
    if (Math.abs(totalDebit - totalCredit) > 0.01) {
      alert('차변과 대변이 같아야 합니다.');
      return false;
    }

    if (formData.lines.some(line => !line.account_id || !line.name)) {
      alert('모든 라인의 계정과목과 설명을 입력해주세요.');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setSubmitting(true);

    try {
      const entryData = {
        ...formData,
        amount_total: formData.lines.reduce((sum, line) => sum + (line.debit || 0), 0),
        line_count: formData.lines.length
      };

      if (editingEntry) {
        // 수정
        const response = await updateJournalEntry(editingEntry.id, entryData);
        if (response.success) {
          handleCloseModal();
          // 즉시 상태 업데이트
          const updatedEntry = response.data as JournalEntry;
          setEntries(prev => prev.map(e => e.id === editingEntry.id ? updatedEntry : e));
        }
      } else {
        // 생성
        const response = await createJournalEntry(entryData);
        if (response.success) {
          handleCloseModal();
          // 즉시 상태 업데이트
          const newEntry = response.data as JournalEntry;
          setEntries(prev => [...prev, newEntry]);
        }
      }
    } catch (error) {
      console.error('분개장 저장 실패:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (entryId: number) => {
    if (window.confirm('정말로 이 분개장을 삭제하시겠습니까?')) {
      try {
        const response = await deleteJournalEntry(entryId);
        if (response.success) {
          // 즉시 상태 업데이트
          setEntries(prev => prev.filter(e => e.id !== entryId));
        }
      } catch (error) {
        console.error('분개장 삭제 실패:', error);
      }
    }
  };

  const handleViewDetail = (entry: JournalEntry) => {
    setSelectedEntry(entry);
    setShowDetailModal(true);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">분개장 관리</h1>
          <p className="mt-1 text-sm text-gray-500">
            회계 분개장을 관리하고 조회하세요.
          </p>
        </div>
        <button
          onClick={() => handleOpenModal()}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          새 분개장
        </button>
      </div>

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {entries.map((entry) => (
            <li key={entry.id} className="px-6 py-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="h-8 w-8 rounded-full bg-green-100 flex items-center justify-center">
                      <span className="text-sm font-medium text-green-800">
                        {entry.name.charAt(0)}
                      </span>
                    </div>
                  </div>
                  <div className="ml-4">
                    <div className="text-sm font-medium text-gray-900">
                      {entry.name}
                    </div>
                    <div className="text-sm text-gray-500">
                      {entry.date} • {entry.state} • {entry.line_count}개 라인
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-sm font-medium text-gray-900">
                    ₩{entry.amount_total?.toLocaleString() || '0'}
                  </span>
                  <button
                    onClick={() => handleViewDetail(entry)}
                    className="text-gray-400 hover:text-gray-500"
                    title="상세보기"
                  >
                    <EyeIcon className="h-4 w-4" />
                  </button>
                  <button
                    onClick={() => handleOpenModal(entry)}
                    className="text-gray-400 hover:text-gray-500"
                    title="수정"
                  >
                    <PencilIcon className="h-4 w-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(entry.id)}
                    className="text-red-400 hover:text-red-500"
                    title="삭제"
                  >
                    <TrashIcon className="h-4 w-4" />
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* 분개장 생성/수정 모달 */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-10 mx-auto p-5 border w-4/5 max-w-4xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">
                  {editingEntry ? '분개장 수정' : '새 분개장'}
                </h3>
                <button
                  onClick={handleCloseModal}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>

              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      분개명
                    </label>
                    <input
                      type="text"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      분개일
                    </label>
                    <input
                      type="date"
                      value={formData.date}
                      onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      참조번호
                    </label>
                    <input
                      type="text"
                      value={formData.ref}
                      onChange={(e) => setFormData({ ...formData, ref: e.target.value })}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    />
                  </div>
                </div>

                <div className="border-t pt-4">
                  <div className="flex justify-between items-center mb-4">
                    <h4 className="text-md font-medium text-gray-900">분개 라인</h4>
                    <button
                      type="button"
                      onClick={handleAddLine}
                      className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-primary-600 bg-primary-100 hover:bg-primary-200"
                    >
                      <PlusCircleIcon className="h-4 w-4 mr-1" />
                      라인 추가
                    </button>
                  </div>

                  <div className="space-y-3">
                    {formData.lines.map((line, index) => (
                      <div key={index} className="grid grid-cols-12 gap-2 items-center">
                        <div className="col-span-4">
                          <select
                            value={line.account_id}
                            onChange={(e) => handleLineChange(index, 'account_id', parseInt(e.target.value))}
                            className="block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                            required
                          >
                            <option value={0}>계정과목 선택</option>
                            {accounts.map((account) => (
                              <option key={account.id} value={account.id}>
                                {account.code} - {account.name}
                              </option>
                            ))}
                          </select>
                        </div>
                        <div className="col-span-4">
                          <input
                            type="text"
                            placeholder="설명"
                            value={line.name}
                            onChange={(e) => handleLineChange(index, 'name', e.target.value)}
                            className="block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                            required
                          />
                        </div>
                        <div className="col-span-2">
                          <input
                            type="number"
                            placeholder="차변"
                            value={line.debit}
                            onChange={(e) => handleLineChange(index, 'debit', parseFloat(e.target.value) || 0)}
                            className="block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                          />
                        </div>
                        <div className="col-span-2">
                          <input
                            type="number"
                            placeholder="대변"
                            value={line.credit}
                            onChange={(e) => handleLineChange(index, 'credit', parseFloat(e.target.value) || 0)}
                            className="block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                          />
                        </div>
                        {formData.lines.length > 1 && (
                          <button
                            type="button"
                            onClick={() => handleRemoveLine(index)}
                            className="text-red-400 hover:text-red-500"
                          >
                            <MinusCircleIcon className="h-4 w-4" />
                          </button>
                        )}
                      </div>
                    ))}
                  </div>

                  <div className="mt-4 p-3 bg-gray-50 rounded-md">
                    <div className="flex justify-between text-sm">
                      <span>차변 합계: ₩{formData.lines.reduce((sum, line) => sum + (line.debit || 0), 0).toLocaleString()}</span>
                      <span>대변 합계: ₩{formData.lines.reduce((sum, line) => sum + (line.credit || 0), 0).toLocaleString()}</span>
                    </div>
                  </div>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={handleCloseModal}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
                  >
                    취소
                  </button>
                  <button
                    type="submit"
                    disabled={submitting}
                    className="px-4 py-2 text-sm font-medium text-white bg-primary-600 border border-transparent rounded-md hover:bg-primary-700 disabled:opacity-50"
                  >
                    {submitting ? '저장 중...' : (editingEntry ? '수정' : '생성')}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* 분개장 상세보기 모달 */}
      {showDetailModal && selectedEntry && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-4/5 max-w-4xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">분개장 상세</h3>
                <button
                  onClick={() => setShowDetailModal(false)}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>

              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">분개명</label>
                    <p className="mt-1 text-sm text-gray-900">{selectedEntry.name}</p>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">분개일</label>
                    <p className="mt-1 text-sm text-gray-900">{selectedEntry.date}</p>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">상태</label>
                    <p className="mt-1 text-sm text-gray-900">{selectedEntry.state}</p>
                  </div>
                </div>

                <div className="border-t pt-4">
                  <h4 className="text-md font-medium text-gray-900 mb-3">분개 라인</h4>
                  <div className="bg-gray-50 rounded-md p-4">
                    <p className="text-sm text-gray-500">분개 라인 상세 정보가 여기에 표시됩니다.</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default JournalEntries;
