import axios from 'axios';
import { ApiResponse, Account, JournalEntry, Partner, Asset, Budget, Currency } from '../types';

const API_BASE_URL = '/api/accounting';

// Mock 데이터
const mockAccounts: Account[] = [
  { id: 1, code: '1000', name: '현금', type: 'asset' },
  { id: 2, code: '1100', name: '은행예금', type: 'asset' },
  { id: 3, code: '2000', name: '미지급금', type: 'liability' },
  { id: 4, code: '3000', name: '자본금', type: 'equity' },
];

const mockJournalEntries: JournalEntry[] = [
  { id: 1, name: '초기 자본 투입', date: '2024-01-15', ref: 'JE001', state: 'posted', amount_total: 1000000, line_count: 2 },
  { id: 2, name: '은행 예금', date: '2024-01-16', ref: 'JE002', state: 'posted', amount_total: 5000000, line_count: 2 },
];

// localStorage에서 Mock 데이터를 가져오거나 초기값 사용
const getMockPartners = (): Partner[] => {
  const stored = localStorage.getItem('odoo_frontend_mock_partners');
  console.log('localStorage에서 읽은 데이터:', stored);
  if (stored && stored !== 'null' && stored !== 'undefined') {
    try {
      const parsed = JSON.parse(stored);
      console.log('파싱된 데이터:', parsed);
      if (Array.isArray(parsed) && parsed.length > 0) {
        console.log('localStorage에서 유효한 데이터를 찾았습니다.');
        return parsed;
      }
    } catch (error) {
      console.error('JSON 파싱 오류:', error);
      localStorage.removeItem('odoo_frontend_mock_partners');
    }
  }
  console.log('localStorage에 데이터가 없어 초기값 사용');
  return [
    { id: 1, name: 'ABC 회사', email: 'abc@example.com', phone: '02-1234-5678', type: 'customer', active: true },
    { id: 2, name: 'XYZ 공급업체', email: 'xyz@example.com', phone: '02-9876-5432', type: 'supplier', active: true },
  ];
};

let mockPartners: Partner[] = getMockPartners();

// Mock 데이터를 localStorage에 저장하는 함수
const saveMockPartners = (partners: Partner[]) => {
  console.log('localStorage에 저장 중:', partners);
  try {
    localStorage.setItem('odoo_frontend_mock_partners', JSON.stringify(partners));
    console.log('localStorage 저장 완료');
  } catch (error) {
    console.error('localStorage 저장 오류:', error);
  }
};

const mockAssets: Asset[] = [
  { id: 1, name: '컴퓨터', code: 'AST001', category: '전자기기', purchase_date: '2024-01-01', purchase_value: 2000000, current_value: 1500000, depreciation_method: 'straight_line' },
  { id: 2, name: '사무용 책상', code: 'AST002', category: '가구', purchase_date: '2024-01-01', purchase_value: 500000, current_value: 400000, depreciation_method: 'straight_line' },
];

const mockBudgets: Budget[] = [
  { id: 1, name: '2024년 예산', fiscal_year: '2024', amount: 10000000, spent_amount: 3000000, remaining_amount: 7000000, state: 'active' },
  { id: 2, name: '마케팅 예산', fiscal_year: '2024', amount: 2000000, spent_amount: 800000, remaining_amount: 1200000, state: 'active' },
];

// axios 인스턴스 생성
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Health Check
export const checkHealth = async (): Promise<ApiResponse> => {
  try {
    const response = await api.get('/health');
    return response.data;
  } catch (error) {
    // Mock 데이터로 성공 응답
    return { success: true, message: 'Mock 모드로 실행 중' };
  }
};

// 계정과목 API
export const getAccounts = async (): Promise<ApiResponse<Account[]>> => {
  try {
    const response = await api.get('/accounts');
    return response.data;
  } catch (error) {
    // Mock 데이터 반환
    return { success: true, data: mockAccounts };
  }
};

export const createAccount = async (account: Partial<Account>): Promise<ApiResponse<Account>> => {
  try {
    const response = await api.post('/accounts', account);
    return response.data;
  } catch (error) {
    // Mock 데이터로 성공 응답
    const newAccount = { ...account, id: Date.now() } as Account;
    return { success: true, data: newAccount };
  }
};

export const updateAccount = async (id: number, account: Partial<Account>): Promise<ApiResponse> => {
  try {
    const response = await api.put(`/accounts/${id}`, account);
    return response.data;
  } catch (error) {
    return { success: true, message: 'Mock 모드에서 수정됨' };
  }
};

export const deleteAccount = async (id: number): Promise<ApiResponse> => {
  try {
    const response = await api.delete(`/accounts/${id}`);
    return response.data;
  } catch (error) {
    return { success: true, message: 'Mock 모드에서 삭제됨' };
  }
};

// 분개장 API
export const getJournalEntries = async (): Promise<ApiResponse<JournalEntry[]>> => {
  try {
    const response = await api.get('/journal-entries');
    return response.data;
  } catch (error) {
    // Mock 데이터 반환
    return { success: true, data: mockJournalEntries };
  }
};

export const createJournalEntry = async (entry: any): Promise<ApiResponse<JournalEntry>> => {
  try {
    const response = await api.post('/journal-entries', entry);
    return response.data;
  } catch (error) {
    // Mock 데이터로 성공 응답
    const newEntry = { ...entry, id: Date.now() } as JournalEntry;
    return { success: true, data: newEntry };
  }
};

// 거래처 API
export const getPartners = async (): Promise<ApiResponse<Partner[]>> => {
  try {
    const response = await api.get('/partners');
    return response.data;
  } catch (error) {
    // localStorage에서 최신 Mock 데이터 가져오기
    const stored = localStorage.getItem('odoo_frontend_mock_partners');
    console.log('getPartners - localStorage 원본 데이터:', stored);
    
    if (stored && stored !== 'null' && stored !== 'undefined') {
      try {
        const parsed = JSON.parse(stored);
        console.log('getPartners - 파싱된 데이터:', parsed);
        if (Array.isArray(parsed) && parsed.length > 0) {
          mockPartners = parsed;
          console.log('getPartners - localStorage에서 데이터 사용:', mockPartners);
          return { success: true, data: mockPartners };
        }
      } catch (error) {
        console.error('getPartners - JSON 파싱 오류:', error);
      }
    }
    
    // localStorage에 데이터가 없으면 초기값 사용
    mockPartners = getMockPartners();
    console.log('getPartners - 초기값 사용:', mockPartners);
    return { success: true, data: mockPartners };
  }
};

export const createPartner = async (partner: Partial<Partner>): Promise<ApiResponse<Partner>> => {
  try {
    const response = await api.post('/partners', partner);
    return response.data;
  } catch (error) {
    // Mock 데이터에 추가
    const newPartner = { ...partner, id: Date.now() } as Partner;
    mockPartners.push(newPartner);
    saveMockPartners(mockPartners);
    return { success: true, data: newPartner };
  }
};

export const updatePartner = async (id: number, partner: Partial<Partner>): Promise<ApiResponse<Partner>> => {
  try {
    const response = await api.put(`/partners/${id}`, partner);
    return response.data;
  } catch (error) {
    // Mock 데이터 업데이트
    console.log('Mock 데이터 업데이트 중:', id, partner);
    console.log('현재 Mock 데이터:', mockPartners);
    const index = mockPartners.findIndex(p => p.id === id);
    console.log('찾은 인덱스:', index);
    if (index !== -1) {
      mockPartners[index] = { ...mockPartners[index], ...partner };
      console.log('업데이트된 Mock 데이터:', mockPartners);
    } else {
      // ID가 없으면 새로 추가
      console.log('ID를 찾을 수 없어 새로 추가합니다.');
      const newPartner = { ...partner, id } as Partner;
      mockPartners.push(newPartner);
    }
    saveMockPartners(mockPartners);
    const updatedPartner = { ...partner, id } as Partner;
    return { success: true, data: updatedPartner };
  }
};

export const deletePartner = async (id: number): Promise<ApiResponse> => {
  try {
    const response = await api.delete(`/partners/${id}`);
    return response.data;
  } catch (error) {
    // Mock 데이터에서 삭제
    mockPartners = mockPartners.filter(p => p.id !== id);
    saveMockPartners(mockPartners);
    return { success: true, message: 'Mock 모드에서 삭제됨' };
  }
};

// 고정자산 API
export const getAssets = async (): Promise<ApiResponse<Asset[]>> => {
  try {
    const response = await api.get('/assets');
    return response.data;
  } catch (error) {
    // Mock 데이터 반환
    return { success: true, data: mockAssets };
  }
};

export const createAsset = async (asset: Partial<Asset>): Promise<ApiResponse<Asset>> => {
  try {
    const response = await api.post('/assets', asset);
    return response.data;
  } catch (error) {
    // Mock 데이터로 성공 응답
    const newAsset = { ...asset, id: Date.now() } as Asset;
    return { success: true, data: newAsset };
  }
