    return response.data;
  } catch (error) {
    // Mock 데이터 반환
    return { success: true, data: mockBudgets };
  }
};

// 통화 API
export const getCurrencies = async (): Promise<ApiResponse<Currency[]>> => {
  try {
    const response = await api.get('/currencies');
    return response.data;
  } catch (error) {
    // Mock 데이터 반환
    return { success: true, data: [
      { id: 1, name: 'KRW', code: 'KRW', symbol: '₩', rate: 1.0, active: true },
      { id: 2, name: 'USD', code: 'USD', symbol: '$', rate: 1300.0, active: true },
