import React, { useState, useEffect } from 'react';
import { getAssets, depreciateAssets } from '../services/api.ts';
import { Asset } from '../types';

const Assets: React.FC = () => {
  const [assets, setAssets] = useState<Asset[]>([]);
  const [loading, setLoading] = useState(false);
  const [deprResult, setDeprResult] = useState<any[]>([]);
  const [fetching, setFetching] = useState(true);

  useEffect(() => {
    fetchAssets();
  }, []);

  const fetchAssets = async () => {
    setFetching(true);
    try {
      const response = await getAssets();
      if (response.success) {
        setAssets(response.data || []);
      }
    } catch (error) {
      console.error('고정자산 조회 실패:', error);
    } finally {
      setFetching(false);
    }
  };

  const handleDepreciate = async (assetId?: number) => {
    setLoading(true);
    setDeprResult([]);
    try {
      const ids = assetId ? [assetId] : assets.map(a => a.id);
      const res = await depreciateAssets(ids);
      if (res.success) setDeprResult(res.data || []);
    } catch (e) {
      alert('감가상각 실행 실패');
    } finally {
      setLoading(false);
    }
  };

  if (fetching) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">고정자산 관리</h1>
        <p className="mt-1 text-sm text-gray-500">고정자산 정보를 조회하고 감가상각을 실행할 수 있습니다.</p>
      </div>

      <div className="flex items-center mb-2">
        <button
          onClick={() => handleDepreciate()}
          className="px-4 py-2 bg-blue-600 text-white rounded shadow disabled:opacity-50"
          disabled={loading}
        >
          {loading ? '계산 중...' : '전체 감가상각'}
        </button>
      </div>

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <table className="min-w-full table-fixed">
          <thead>
            <tr className="bg-gray-50">
              <th className="px-4 py-2 w-1/6">자산명</th>
              <th className="px-4 py-2 w-1/6">취득가</th>
              <th className="px-4 py-2 w-1/6">취득일</th>
              <th className="px-4 py-2 w-1/6">감가상각방법</th>
              <th className="px-4 py-2 w-1/6">잔존가치</th>
              <th className="px-4 py-2 w-1/6">작업</th>
            </tr>
          </thead>
          <tbody>
            {assets.map(asset => (
              <tr key={asset.id} className="border-b">
                <td className="px-4 py-2">{asset.name}</td>
                <td className="px-4 py-2">₩{asset.purchase_value?.toLocaleString() || '0'}</td>
                <td className="px-4 py-2">{asset.purchase_date || '-'}</td>
                <td className="px-4 py-2">{asset.depreciation_method}</td>
                <td className="px-4 py-2">₩{asset.current_value?.toLocaleString() || '0'}</td>
                <td className="px-4 py-2">
                  <button
                    onClick={() => handleDepreciate(asset.id)}
                    className="px-2 py-1 bg-gray-100 rounded border border-gray-300 hover:bg-blue-100 disabled:opacity-50"
                    disabled={loading}
                  >
                    감가상각
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {deprResult.length > 0 && (
        <div className="mt-8">
          <h3 className="font-bold mb-2">감가상각 결과 (분개장)</h3>
          <table className="min-w-full table-fixed border">
            <thead>
              <tr className="bg-gray-50">
                <th className="px-4 py-2 w-1/6">자산명</th>
                <th className="px-4 py-2 w-1/6">감가상각액</th>
                <th className="px-4 py-2 w-1/6">일자</th>
                <th className="px-4 py-2 w-1/6">전표번호</th>
                <th className="px-4 py-2 w-2/6">분개라인</th>
              </tr>
            </thead>
            <tbody>
              {deprResult.map((res: any, idx: number) => (
                <tr key={idx} className="border-b">
                  <td className="px-4 py-2">{assets.find(a => a.id === res.asset_id)?.name || '-'}</td>
                  <td className="px-4 py-2">₩{res.depreciation_amount?.toLocaleString() || '-'}</td>
                  <td className="px-4 py-2">{res.date || '-'}</td>
                  <td className="px-4 py-2">{res.journal_entry?.ref || '-'}</td>
                  <td className="px-4 py-2">
                    {res.journal_entry?.lines?.map((line: any, i: number) => (
                      <div key={i} className="text-xs text-gray-700">
                        {line.account_name}: 차변 {line.debit}, 대변 {line.credit}
                      </div>
                    ))}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Assets;
