import React, { useState, useEffect } from 'react';
import { PlusIcon, PencilIcon, TrashIcon, XMarkIcon, EyeIcon, DocumentTextIcon } from '@heroicons/react/24/outline';
import { getTaxReports, createTaxReport, updateTaxReport, deleteTaxReport, generateTaxReportData, getTaxes } from '../services/api.ts';
import { TaxReport, Tax } from '../types';

interface TaxReportFormData {
  name: string;
  period: string;
  report_type: 'monthly' | 'quarterly' | 'yearly';
  tax_ids: number[]; // 선택된 세금 ID들
}

const TaxReports: React.FC = () => {
  const [taxReports, setTaxReports] = useState<TaxReport[]>([]);
  const [taxes, setTaxes] = useState<Tax[]>([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState<TaxReport | null>(null);
  const [editingReport, setEditingReport] = useState<TaxReport | null>(null);
  const [formData, setFormData] = useState<TaxReportFormData>({
    name: '',
    period: '',
    report_type: 'monthly',
    tax_ids: [],
  });
  const [submitting, setSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string>('');
  const [successMessage, setSuccessMessage] = useState<string>('');
  const [stateFilter, setStateFilter] = useState<'all' | 'draft' | 'confirmed' | 'submitted'>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  useEffect(() => {
    fetchTaxReports();
    fetchTaxes();
  }, []);

  const fetchTaxReports = async () => {
    try {
      const response = await getTaxReports();
      if (response.success) {
        setTaxReports(response.data || []);
      } else {
        setErrorMessage(response.message || '세금 신고서 목록을 불러오는데 실패했습니다.');
      }
    } catch (error) {
      console.error('세금 신고서 조회 실패:', error);
      setErrorMessage('세금 신고서 목록을 불러오는데 실패했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const fetchTaxes = async () => {
    try {
      const response = await getTaxes();
      if (response.success) {
        setTaxes(response.data || []);
      }
    } catch (error) {
      console.error('세금 조회 실패:', error);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      period: '',
      report_type: 'monthly',
      tax_ids: [],
    });
    setEditingReport(null);
    setErrorMessage('');
    setSuccessMessage('');
  };

  const handleOpenModal = (report?: TaxReport) => {
    if (report) {
      setEditingReport(report);
      setFormData({
        name: report.name,
        period: report.period,
        report_type: report.report_type,
        tax_ids: report.taxes.map(tax => tax.id), // 기존 신고서의 세금 ID들을 가져옴
      });
    } else {
      resetForm();
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    resetForm();
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.name.trim()) {
      setErrorMessage('신고서명은 필수입니다.');
      return;
    }

    if (!formData.period) {
      setErrorMessage('신고기간은 필수입니다.');
      return;
    }

    setSubmitting(true);
    setErrorMessage('');
    setSuccessMessage('');

    try {
      if (editingReport) {
        const response = await updateTaxReport(editingReport.id, formData);
        if (response.success) {
          setSuccessMessage('세금 신고서가 성공적으로 수정되었습니다.');
          await fetchTaxReports();
          handleCloseModal();
        } else {
          setErrorMessage(response.message || '세금 신고서 수정에 실패했습니다.');
        }
      } else {
        const response = await createTaxReport(formData);
        if (response.success) {
          setSuccessMessage('세금 신고서가 성공적으로 생성되었습니다.');
          await fetchTaxReports();
          handleCloseModal();
        } else {
          setErrorMessage(response.message || '세금 신고서 생성에 실패했습니다.');
        }
      }
    } catch (error) {
      console.error('세금 신고서 저장 실패:', error);
      setErrorMessage('세금 신고서 저장 중 오류가 발생했습니다.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (reportId: number) => {
    if (window.confirm('정말로 이 세금 신고서를 삭제하시겠습니까?')) {
      try {
        const response = await deleteTaxReport(reportId);
        if (response.success) {
          setSuccessMessage('세금 신고서가 성공적으로 삭제되었습니다.');
          await fetchTaxReports();
          
          setTimeout(() => {
            setSuccessMessage('');
          }, 3000);
        } else {
          setErrorMessage(response.message || '세금 신고서 삭제에 실패했습니다.');
        }
      } catch (error) {
        console.error('세금 신고서 삭제 실패:', error);
        setErrorMessage('세금 신고서 삭제 중 오류가 발생했습니다.');
      }
    }
  };

  const handleGenerateData = async (reportId: number) => {
    try {
      const response = await generateTaxReportData(reportId);
      if (response.success) {
        setSuccessMessage('신고서 데이터가 성공적으로 생성되었습니다.');
        await fetchTaxReports();
        
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      } else {
        setErrorMessage(response.message || '신고서 데이터 생성에 실패했습니다.');
      }
    } catch (error) {
      console.error('신고서 데이터 생성 실패:', error);
      setErrorMessage('신고서 데이터 생성 중 오류가 발생했습니다.');
    }
  };

  const handleViewDetail = (report: TaxReport) => {
    setSelectedReport(report);
    setShowDetailModal(true);
  };

  const getStateLabel = (state: string) => {
    switch (state) {
      case 'draft': return '작성중';
      case 'confirmed': return '확정';
      case 'submitted': return '신고완료';
      default: return state;
    }
  };

  const getStateColor = (state: string) => {
    switch (state) {
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'confirmed': return 'bg-blue-100 text-blue-800';
      case 'submitted': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getReportTypeLabel = (type: string) => {
    switch (type) {
      case 'monthly': return '월별';
      case 'quarterly': return '분기별';
      case 'yearly': return '연간';
      default: return type;
    }
  };

  // 필터링된 신고서 목록 계산
  const filteredReports = taxReports.filter(report => {
    const matchesFilter = stateFilter === 'all' || report.state === stateFilter;
    
    const matchesSearch = !searchTerm || 
      report.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      report.period.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesFilter && matchesSearch;
  });

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('ko-KR').format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* 성공/에러 메시지 */}
      {successMessage && (
        <div className="bg-green-50 border border-green-200 text-green-800 px-4 py-3 rounded relative">
          <span className="block sm:inline">{successMessage}</span>
        </div>
      )}
      
      {errorMessage && (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded relative">
          <span className="block sm:inline">{errorMessage}</span>
        </div>
      )}

      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">세금 신고서 관리</h1>
          <p className="mt-1 text-sm text-gray-500">
            세금 신고서를 생성하고 관리하세요.
          </p>
        </div>
        <button
          onClick={() => handleOpenModal()}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          새 신고서
        </button>
      </div>

      {/* 필터 및 검색 */}
      <div className="flex items-center space-x-4">
        <div className="flex items-center space-x-2">
          <label className="text-sm font-medium text-gray-700">상태:</label>
          <select
            value={stateFilter}
            onChange={(e) => setStateFilter(e.target.value as 'all' | 'draft' | 'confirmed' | 'submitted')}
            className="border border-gray-300 rounded-md px-3 py-1 text-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="all">전체</option>
            <option value="draft">작성중</option>
            <option value="confirmed">확정</option>
            <option value="submitted">신고완료</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <label className="text-sm font-medium text-gray-700">검색:</label>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="신고서명 또는 기간 검색"
            className="border border-gray-300 rounded-md px-3 py-1 text-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
          />
        </div>
      </div>

      {/* 신고서 목록 */}
      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                신고서명
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                기간
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                유형
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                납부세액
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                작업
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredReports.map((report) => (
              <tr key={report.id}>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">
                    {report.name}
                  </div>
                  <div className="text-sm text-gray-500">
                    생성일: {formatDate(report.created_at)}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {report.period}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {getReportTypeLabel(report.report_type)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStateColor(report.state)}`}>
                    {getStateLabel(report.state)}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  <span className={report.net_tax < 0 ? 'text-red-600' : ''}>
                    {formatCurrency(report.net_tax)}원
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => handleViewDetail(report)}
                      className="text-blue-400 hover:text-blue-500"
                      title="상세보기"
                    >
                      <EyeIcon className="h-4 w-4" />
                    </button>
                    {report.state === 'draft' && (
                      <>
                        <button
                          onClick={() => handleOpenModal(report)}
                          className="text-gray-400 hover:text-gray-500"
                          title="수정"
                        >
                          <PencilIcon className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => handleGenerateData(report.id)}
                          className="text-green-400 hover:text-green-500"
                          title="데이터 생성"
                        >
                          <DocumentTextIcon className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => handleDelete(report.id)}
                          className="text-red-400 hover:text-red-500"
                          title="삭제"
                        >
                          <TrashIcon className="h-4 w-4" />
                        </button>
                      </>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* 신고서 생성/수정 모달 */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">
                  {editingReport ? '신고서 수정' : '새 신고서'}
                </h3>
                <button
                  onClick={handleCloseModal}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>

              {errorMessage && (
                <div className="mb-4 bg-red-50 border border-red-200 text-red-800 px-3 py-2 rounded text-sm">
                  {errorMessage}
                </div>
              )}

              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    신고서명 *
                  </label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    required
                    disabled={submitting}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    신고기간 *
                  </label>
                  <input
                    type="month"
                    value={formData.period}
                    onChange={(e) => setFormData({ ...formData, period: e.target.value })}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    required
                    disabled={submitting}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    신고서 유형 *
                  </label>
                  <select
                    value={formData.report_type}
                    onChange={(e) => setFormData({ ...formData, report_type: e.target.value as any })}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    required
                    disabled={submitting}
                  >
                    <option value="monthly">월별</option>
                    <option value="quarterly">분기별</option>
                    <option value="yearly">연간</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    세금 선택 *
                  </label>
                  <select
                    multiple
                    value={formData.tax_ids}
                    onChange={(e) => setFormData({ ...formData, tax_ids: Array.from(e.target.selectedOptions, (option) => parseInt(option.value, 10)) })}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    required
                    disabled={submitting}
                  >
                    {taxes.map((tax) => (
                      <option key={tax.id} value={tax.id}>
                        {tax.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={handleCloseModal}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
                    disabled={submitting}
                  >
                    취소
                  </button>
                  <button
                    type="submit"
                    disabled={submitting}
                    className="px-4 py-2 text-sm font-medium text-white bg-primary-600 border border-transparent rounded-md hover:bg-primary-700 disabled:opacity-50 flex items-center"
                  >
                    {submitting && (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    )}
                    {submitting ? '저장 중...' : (editingReport ? '수정' : '생성')}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* 신고서 상세 모달 */}
      {showDetailModal && selectedReport && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-2xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">신고서 상세</h3>
                <button
                  onClick={() => setShowDetailModal(false)}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>

              <div className="space-y-6">
                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-2">기본 정보</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <span className="text-sm text-gray-500">신고서명:</span>
                      <div className="text-sm font-medium">{selectedReport.name}</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">기간:</span>
                      <div className="text-sm font-medium">{selectedReport.period}</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">유형:</span>
                      <div className="text-sm font-medium">{getReportTypeLabel(selectedReport.report_type)}</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">상태:</span>
                      <div className="text-sm font-medium">
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStateColor(selectedReport.state)}`}>
                          {getStateLabel(selectedReport.state)}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-2">세금 정보</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <span className="text-sm text-gray-500">매출세액:</span>
                      <div className="text-sm font-medium">{formatCurrency(selectedReport.total_sales_tax)}원</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">매입세액:</span>
                      <div className="text-sm font-medium">{formatCurrency(selectedReport.total_purchase_tax)}원</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">납부세액:</span>
                      <div className="text-sm font-medium">{formatCurrency(selectedReport.payable_tax)}원</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">환급세액:</span>
                      <div className="text-sm font-medium text-red-600">{formatCurrency(selectedReport.refundable_tax)}원</div>
                    </div>
                    <div className="col-span-2">
                      <span className="text-sm text-gray-500">순납부세액:</span>
                      <div className={`text-lg font-bold ${selectedReport.net_tax < 0 ? 'text-red-600' : ''}`}>
                        {formatCurrency(selectedReport.net_tax)}원
                      </div>
                    </div>
                  </div>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-2">금액 정보</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <span className="text-sm text-gray-500">매출금액:</span>
                      <div className="text-sm font-medium">{formatCurrency(selectedReport.total_sales_amount)}원</div>
                    </div>
                    <div>
                      <span className="text-sm text-gray-500">매입금액:</span>
                      <div className="text-sm font-medium">{formatCurrency(selectedReport.total_purchase_amount)}원</div>
                    </div>
                  </div>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-2">일정 정보</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <span className="text-sm text-gray-500">생성일:</span>
                      <div className="text-sm font-medium">{formatDate(selectedReport.created_at)}</div>
                    </div>
                    {selectedReport.submitted_at && (
                      <div>
                        <span className="text-sm text-gray-500">신고일:</span>
                        <div className="text-sm font-medium">{formatDate(selectedReport.submitted_at)}</div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TaxReports;
