import React, { useState, useEffect } from 'react';
import { PlusIcon, PencilIcon, TrashIcon, XMarkIcon, EyeIcon, DocumentTextIcon } from '@heroicons/react/24/outline';
import { getTaxReports, createTaxReport, updateTaxReport, deleteTaxReport, generateTaxReportData, getTaxes } from '../services/api.ts';
import { TaxReport, Tax } from '../types';

interface TaxReportFormData {
  name: string;
  report_type: 'monthly' | 'quarterly' | 'yearly' | 'half_yearly';
  additional_period?: string; // Î∂ÑÍ∏∞Î≥Ñ/Ïó∞Í∞Ñ/Î∞òÍ∏∞Î≥Ñ Ï∂îÍ∞Ä ÏÑ†ÌÉù
  tax_ids: number[]; // ÏÑ†ÌÉùÎêú ÏÑ∏Í∏à IDÎì§
  selected_taxes?: string[]; // ÏÑ†ÌÉùÎêú ÏÑ∏Í∏àÎ™ÖÎì§
  vat_payable?: number; // ÎÇ©Î∂ÄÏÑ∏Ïï°
  start_date?: string; // ÏãúÏûëÏùº
  end_date?: string; // Ï¢ÖÎ£åÏùº
}

const TaxReports: React.FC = () => {
  const [taxReports, setTaxReports] = useState<TaxReport[]>([]);
  const [taxes, setTaxes] = useState<Tax[]>([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState<TaxReport | null>(null);
  const [editingReport, setEditingReport] = useState<TaxReport | null>(null);
  const [formData, setFormData] = useState<TaxReportFormData>({
    name: '',
    report_type: 'monthly',
    additional_period: '',
    tax_ids: [],
    vat_payable: 0,
    start_date: '',
    end_date: '',
  });
  const [submitting, setSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string>('');
  const [successMessage, setSuccessMessage] = useState<string>('');
  const [stateFilter, setStateFilter] = useState<'all' | 'draft' | 'confirmed' | 'submitted'>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  useEffect(() => {
    fetchTaxReports();
    fetchTaxes();
  }, []);

  const fetchTaxReports = async () => {
    try {
      const response = await getTaxReports();
      console.log('üîç ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Î™©Î°ù ÏùëÎãµ:', response);
      if (response.success) {
        console.log('üìä ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Îç∞Ïù¥ÌÑ∞:', response.data);
        setTaxReports(response.data || []);
      } else {
        setErrorMessage(response.message || 'ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Ï°∞Ìöå Ïã§Ìå®:', error);
      setErrorMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const fetchTaxes = async () => {
    try {
      const response = await getTaxes();
      if (response.success) {
        setTaxes(response.data || []);
      }
    } catch (error) {
      console.error('ÏÑ∏Í∏à Ï°∞Ìöå Ïã§Ìå®:', error);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      report_type: 'monthly',
      additional_period: '',
      tax_ids: [],
      vat_payable: 0,
      start_date: '',
      end_date: '',
    });
    setEditingReport(null);
    setErrorMessage('');
    setSuccessMessage('');
  };

  const handleOpenModal = (report?: TaxReport) => {
    if (report) {
      setEditingReport(report);
      setFormData({
        name: report.name,
        report_type: 'monthly', // Í∏∞Î≥∏Í∞íÏúºÎ°ú ÏÑ§Ï†ï
        tax_ids: [], // Í∏∞Ï°¥ Ïã†Í≥†ÏÑúÏùò ÏÑ∏Í∏à IDÎì§ (Î∞±ÏóîÎìúÏóêÏÑú Ï†úÍ≥µ)
        vat_payable: report.vat_payable || 0,
        start_date: '', // Í∏∞Ï°¥ Ïã†Í≥†ÏÑúÎäî ÏãúÏûëÏùº/Ï¢ÖÎ£åÏùºÏùÑ Î≥ÑÎèÑÎ°ú ÏÑ§Ï†ïÌïòÏßÄ ÏïäÏùå
        end_date: '',
      });
    } else {
      resetForm();
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    resetForm();
  };



  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.name.trim()) {
      setErrorMessage('Ïã†Í≥†ÏÑúÎ™ÖÏùÄ ÌïÑÏàòÏûÖÎãàÎã§.');
      return;
    }

    if (formData.tax_ids.length === 0) {
      setErrorMessage('ÏÑ∏Í∏àÏùÑ ÌïòÎÇò Ïù¥ÏÉÅ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // Î™®Îì† Ïú†ÌòïÏóêÏÑú Ï∂îÍ∞Ä ÏÑ†ÌÉù Í≤ÄÏ¶ù
    if (!formData.additional_period) {
      setErrorMessage('Í∏∞Í∞ÑÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // ÎÇ©Î∂ÄÏÑ∏Ïï° Í≤ÄÏ¶ù
    if (formData.vat_payable === undefined || formData.vat_payable === null || formData.vat_payable < 0) {
      setErrorMessage('ÎÇ©Î∂ÄÏÑ∏Ïï°ÏùÑ Ïò¨Î∞îÎ•¥Í≤å ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setSubmitting(true);
    setErrorMessage('');
    setSuccessMessage('');

    try {
      if (editingReport) {
        const response = await updateTaxReport(editingReport.id, formData);
        if (response.success) {
          setSuccessMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.');
          await fetchTaxReports();
          handleCloseModal();
        } else {
          setErrorMessage(response.message || 'ÏÑ∏Í∏à Ïã†Í≥†ÏÑú ÏàòÏ†ïÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      } else {
        const response = await createTaxReport(formData);
        if (response.success) {
          setSuccessMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§.');
          await fetchTaxReports();
          
          // ÏÉàÎ°ú ÏÉùÏÑ±Îêú Ïã†Í≥†ÏÑúÏùò IDÎ•º Ï∞æÏïÑÏÑú Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
          const newReports = await getTaxReports();
          if (newReports.success && newReports.data) {
            const latestReport = newReports.data[newReports.data.length - 1];
            if (latestReport && latestReport.id) {
              try {
                await generateTaxReportData(latestReport.id);
                await fetchTaxReports(); // Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± ÌõÑ Îã§Ïãú Î™©Î°ù Í∞±Ïã†
                setSuccessMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§. Îç∞Ïù¥ÌÑ∞ÎèÑ ÏûêÎèôÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§.');
              } catch (error) {
                console.error('Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± Ïã§Ìå®:', error);
                setSuccessMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§. (Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±ÏùÄ Î≥ÑÎèÑÎ°ú ÏßÑÌñâÌï¥Ï£ºÏÑ∏Ïöî)');
              }
            }
          }
          
          handleCloseModal();
        } else {
          setErrorMessage(response.message || 'ÏÑ∏Í∏à Ïã†Í≥†ÏÑú ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      }
    } catch (error) {
      console.error('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Ï†ÄÏû• Ïã§Ìå®:', error);
      setErrorMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (reportId: number) => {
    if (window.confirm('Ï†ïÎßêÎ°ú Ïù¥ ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const response = await deleteTaxReport(reportId);
        if (response.success) {
          setSuccessMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
          await fetchTaxReports();
          
          setTimeout(() => {
            setSuccessMessage('');
          }, 3000);
        } else {
          setErrorMessage(response.message || 'ÏÑ∏Í∏à Ïã†Í≥†ÏÑú ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      } catch (error) {
        console.error('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú ÏÇ≠Ï†ú Ïã§Ìå®:', error);
        setErrorMessage('ÏÑ∏Í∏à Ïã†Í≥†ÏÑú ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }
  };

  const handleGenerateData = async (reportId: number) => {
    try {
      const response = await generateTaxReportData(reportId);
      if (response.success) {
        setSuccessMessage('Ïã†Í≥†ÏÑú Îç∞Ïù¥ÌÑ∞Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§.');
        await fetchTaxReports();
        
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      } else {
        setErrorMessage(response.message || 'Ïã†Í≥†ÏÑú Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('Ïã†Í≥†ÏÑú Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± Ïã§Ìå®:', error);
      setErrorMessage('Ïã†Í≥†ÏÑú Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleViewDetail = (report: TaxReport) => {
    setSelectedReport(report);
    setShowDetailModal(true);
  };

  const getStateLabel = (state: string) => {
    switch (state) {
      case 'draft': return 'ÏûëÏÑ±Ï§ë';
      case 'confirmed': return 'ÌôïÏ†ï';
      case 'submitted': return 'Ïã†Í≥†ÏôÑÎ£å';
      default: return state;
    }
  };

  const getStateColor = (state: string) => {
    switch (state) {
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'confirmed': return 'bg-blue-100 text-blue-800';
      case 'submitted': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getReportTypeLabel = (type: string) => {
    switch (type) {
      case 'monthly': return 'ÏõîÎ≥Ñ';
      case 'quarterly': return 'Î∂ÑÍ∏∞Î≥Ñ';
      case 'yearly': return 'Ïó∞Í∞Ñ';
      case 'half_yearly': return 'Î∞òÍ∏∞Î≥Ñ';
      default: return type;
    }
  };

  // ÌïÑÌÑ∞ÎßÅÎêú Ïã†Í≥†ÏÑú Î™©Î°ù Í≥ÑÏÇ∞
  const filteredReports = taxReports.filter(report => {
    const matchesFilter = stateFilter === 'all' || report.state === stateFilter;
    
    const matchesSearch = !searchTerm || 
      report.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      report.tax_period_name.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesFilter && matchesSearch;
  });

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('ko-KR').format(amount);
  };

  const formatDate = (dateString: string) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '-';
      return date.toLocaleDateString('ko-KR');
    } catch (error) {
      return '-';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* ÏÑ±Í≥µ/ÏóêÎü¨ Î©îÏãúÏßÄ */}
      {successMessage && (
        <div className="bg-green-50 border border-green-200 text-green-800 px-4 py-3 rounded relative">
          <span className="block sm:inline">{successMessage}</span>
        </div>
      )}
      
      {errorMessage && (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded relative">
          <span className="block sm:inline">{errorMessage}</span>
        </div>
