import React, { useState, useEffect } from 'react';
import { getAssets } from '../services/api';
import { Asset } from '../types';

const Assets: React.FC = () => {
  const [assets, setAssets] = useState<Asset[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAssets();
  }, []);

  const fetchAssets = async () => {
    try {
      const response = await getAssets();
      if (response.success) {
        setAssets(response.data || []);
      }
    } catch (error) {
      console.error('고정자산 조회 실패:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">고정자산 관리</h1>
        <p className="mt-1 text-sm text-gray-500">
          고정자산 정보를 조회하세요.
        </p>
      </div>

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {assets.map((asset) => (
            <li key={asset.id} className="px-6 py-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="h-8 w-8 rounded-full bg-yellow-100 flex items-center justify-center">
                      <span className="text-sm font-medium text-yellow-800">
                        {asset.name.charAt(0)}
                      </span>
                    </div>
                  </div>
                  <div className="ml-4">
                    <div className="text-sm font-medium text-gray-900">
                      {asset.name}
                    </div>
                    <div className="text-sm text-gray-500">
                      {asset.category} • {asset.depreciation_method}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-sm font-medium text-gray-900">
                    ₩{asset.current_value?.toLocaleString() || '0'}
                  </div>
                  <div className="text-sm text-gray-500">
                    구매가: ₩{asset.purchase_value?.toLocaleString() || '0'}
                  </div>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Assets;
