import axios from 'axios';
import { ApiResponse, Account, JournalEntry, Partner, Asset, Budget, Currency, Tax, TaxCalculation, TaxReport } from '../types';

const API_BASE_URL = '/api/accounting';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// ÏöîÏ≤≠ Ïù∏ÌÑ∞ÏÖâÌÑ∞
api.interceptors.request.use(
  (config) => {
    console.log('üöÄ API ÏöîÏ≤≠:', {
      method: config.method?.toUpperCase(),
      url: config.url,
      data: config.data,
      headers: config.headers,
    });
    return config;
  },
  (error) => {
    console.error('‚ùå ÏöîÏ≤≠ ÏóêÎü¨:', error);
    return Promise.reject(error);
  }
);

// ÏùëÎãµ Ïù∏ÌÑ∞ÏÖâÌÑ∞
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API ÏùëÎãµ:', {
      status: response.status,
      url: response.config.url,
      data: response.data,
    });
    return response;
  },
  (error) => {
    console.error('‚ùå ÏùëÎãµ ÏóêÎü¨:', {
      status: error.response?.status,
      url: error.config?.url,
      data: error.response?.data,
      message: error.message,
    });
    return Promise.reject(error);
  }
);

// Health Check
export const checkHealth = async (): Promise<ApiResponse> => {
